import { Component, OnInit } from '@angular/core';
import { Product } from 'src/app/common/product';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit{

  products: Product[] = [];

  constructor(private productService: ProductService){}

  ngOnInit(): void {
    this.listProduct();
    console.log(this.products);
  }

  listProduct(){
    this.productService.getProductList().subscribe(
      elements =>{
        this.products = elements;
      }
    );
  }

}
<div class="main-content">
    <div class="section-content secttion-conttent-p30">
        <div class="container-fluid">
            <div class="row">
                <div *ngFor="let tempProduct of products" class="col-md-3">

                    <div class="product-box">
                        <img src="{{tempProduct.imgUrl}}" class="img-responsive" style="width: 50px;"/>
                        <h1>{{ tempProduct.brand }}</h1>
                        <div class="price">{{tempProduct.price | currency: 'USD'}}</div>
                        <a href="#" class="primary-btn">Add to cart</a>
                    </div>

                </div>
            </div>
        </div>
    </div>
</div>
